import React, { useState } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

const WeatherCard = ({ place, date, time, weatherDetails }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [saved, setSaved] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const saveWeather = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/weather', {
        place,
        date,
        time,
        weatherDetails
      });
      if (response.status === 200) {
        setSaved(true);
      } else {
        console.error('Failed to save weather data');
      }
    } catch (error) {
      console.error('Error saving weather data:', error);
    }
  };

  return (
    <div>
      <div className="weather-card" onClick={openModal}>
        <h2>{place}</h2>
        <p>{date}</p>
        <p>{time}</p>
        <p>{weatherDetails.temp_c} 째C / {weatherDetails.temp_f} 째F</p>
        <p>{weatherDetails.condition.text}</p>
        {/* Add more weather details as needed */}
      </div>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        <h2>{place}</h2>
        <p>Date: {date}</p>
        <p>Time: {time}</p>
        <p>Temperature: {weatherDetails.temp_c} 째C / {weatherDetails.temp_f} 째F</p>
        <p>Weather: {weatherDetails.condition.text}</p>
        {/* Display more weather details here */}
        <button onClick={saveWeather}>Save to Database</button>
        {saved && <p>Weather data saved!</p>}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default WeatherCard;
