import React, { useState, useEffect } from 'react';
import MapComponent from './MapComponent';
import WeatherCard from './WeatherCard';
import styled from 'styled-components';
import axios from 'axios';

const HomePageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  width: 100%;
`;

const FormWrapper = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  background: rgba(255, 255, 255, 0.8);
  padding: 10px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const WeatherListWrapper = styled.div`
  margin-top: 20px;
  width: 80%;
`;

const HomePage = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [place, setPlace] = useState('');
  const [savedWeatherData, setSavedWeatherData] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false); // New state for modal

  useEffect(() => {
    const fetchSavedWeatherData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/weather/all');
        setSavedWeatherData(response.data);
      } catch (error) {
        console.error('Error fetching saved weather data:', error);
      }
    };

    fetchSavedWeatherData();
  }, []);

  const handleInputChange = (e) => {
    setPlace(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:8080/api/weather/city?place=${place}`);
      setWeatherData(response.data);
      setModalIsOpen(true); // Open modal after fetching weather data
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const handleMarkerClick = async (location) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/weather/location?lat=${location.lat}&lng=${location.lng}`);
      setWeatherData(response.data);
      setModalIsOpen(true); // Open modal after fetching weather data
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <HomePageWrapper>
      <FormWrapper>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={place}
            onChange={handleInputChange}
            placeholder="Enter place name"
          />
          <button type="submit">Get Weather</button>
        </form>
      </FormWrapper>
      {weatherData && (
        <WeatherCard
          place={weatherData.placeName}
          date={weatherData.dateTime.split(' ')[0]}
          time={weatherData.dateTime.split(' ')[1]}
          weatherDetails={weatherData}
          modalIsOpen={modalIsOpen} // Pass modal state to WeatherCard
          closeModal={closeModal}    // Pass closeModal function to WeatherCard
        />
      )}
      <WeatherListWrapper>
        {savedWeatherData.map((data, index) => (
          <WeatherCard
            key={index}
            place={data.placeName}
            date={data.dateTime.split(' ')[0]}
            time={data.dateTime.split(' ')[1]}
            weatherDetails={data}
            modalIsOpen={false} // Ensure this modal does not open for saved data
          />
        ))}
      </WeatherListWrapper>
    </HomePageWrapper>
  );
};

export default HomePage;
