import React, { useState, useCallback } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import axios from 'axios';
import styled from 'styled-components';
import WeatherPopup from './WeatherPopup';

const libraries = ['places'];
const containerStyle = {
  width: '100%',
  height: '600px',
};

const center = {
  lat: 0,
  lng: 0,
};

const HomePage = () => {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY',
    libraries,
  });

  const [map, setMap] = useState(null);
  const [markerPosition, setMarkerPosition] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const [inputPlace, setInputPlace] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  const handleMapClick = (event) => {
    const lat = event.latLng.lat();
    const lng = event.latLng.lng();
    setMarkerPosition({ lat, lng });
    fetchWeatherData(lat, lng);
  };

  const fetchWeatherData = async (lat, lng) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/weather?lat=${lat}&lng=${lng}`);
      setWeatherData(response.data);
      setShowPopup(true);
    } catch (error) {
      console.error('Error fetching weather data', error);
    }
  };

  const handleInputChange = (event) => {
    setInputPlace(event.target.value);
  };

  const handleGetWeatherClick = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/weather?place=${inputPlace}`);
      setWeatherData(response.data);
      setShowPopup(true);
    } catch (error) {
      console.error('Error fetching weather data', error);
    }
  };

  return (
    <Container>
      <InputContainer>
        <input 
          type="text" 
          value={inputPlace} 
          onChange={handleInputChange} 
          placeholder="Enter a place name" 
        />
        <button onClick={handleGetWeatherClick}>Get Weather</button>
      </InputContainer>
      {isLoaded ? (
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={2}
          onClick={handleMapClick}
          onLoad={onLoad}
          onUnmount={onUnmount}
        >
          {markerPosition && <Marker position={markerPosition} />}
        </GoogleMap>
      ) : (
        <p>Loading...</p>
      )}
      {showPopup && weatherData && (
        <WeatherPopup
          weatherData={weatherData}
          onClose={() => setShowPopup(false)}
        />
      )}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const InputContainer = styled.div`
  margin-bottom: 20px;
  input {
    padding: 10px;
    margin-right: 10px;
  }
  button {
    padding: 10px;
  }
`;

export default HomePage;
