import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';
import styled from 'styled-components';

const StyledModal = styled(Modal)`
  background: linear-gradient(135deg, #6DD5FA, #FFFFFF);
  border-radius: 15px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  padding: 20px;
  max-width: 500px;
  margin: auto;
  overflow-y: auto;
  color: #333;
  font-family: 'Arial', sans-serif;
`;

const Title = styled.h2`
  font-size: 2rem;
  font-weight: bold;
  color: #FF6F61;
  text-align: center;
`;

const WeatherDetail = styled.p`
  font-size: 1.2rem;
  margin: 10px 0;
  color: #333;
  text-align: center;
`;

const Button = styled.button`
  background-color: #4B79A1;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  font-size: 1rem;
  cursor: pointer;
  margin: 10px;

  &:hover {
    background-color: #FF6F61;
  }
`;

const SavedMessage = styled.p`
  color: green;
  font-weight: bold;
  text-align: center;
`;

const WeatherCard = ({ place, date, time, weatherDetails }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    if (weatherDetails) {
      setModalIsOpen(true);  // Automatically open the modal when weatherDetails are available
    }
  }, [weatherDetails]);

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const saveWeather = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/weather/save', weatherDetails);
      if (response.status === 200) {
        setSaved(true);
      } else {
        console.error('Failed to save weather data');
      }
    } catch (error) {
      console.error('Error saving weather data:', error);
    }
  };

  return (
    <div>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        <h2>{place}</h2>
        <p>Date: {date}</p>
        <p>Time: {time}</p>
        <p>Temperature: {weatherDetails.tempC} °C</p>
        <p>Weather: {weatherDetails.condition}</p>
        <p>Humidity: {weatherDetails.humidity}%</p>
        <p>Precipitation: {weatherDetails.precipitation} mm</p>
        <p>Wind Speed: {weatherDetails.windSpeed} kph</p>
        <p>UV Index: {weatherDetails.uvIndex}</p>
        <p>Air Quality Index: {weatherDetails.aqi}</p>
        <p>Pressure: {weatherDetails.pressure} mb</p>
        <p>Cloud Cover: {weatherDetails.cloud}%</p>
        <p>Feels Like: {weatherDetails.feelslikeC} °C</p>
        <p>Heat Index: {weatherDetails.heatindexC} °C</p>
        <p>Dew Point: {weatherDetails.dewpointC} °C</p>
        <p>Visibility: {weatherDetails.visKm} km</p>
        <p>Day/Night: {weatherDetails.day}</p>
        <p>Weekday: {weatherDetails.weekday}</p>
        {/* Display more weather details here */}
        <button onClick={saveWeather}>Save to Database</button>
        {saved && <p>Weather data saved!</p>}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default WeatherCard;
